let H;
let W;

let G;

let SEED;

let flocks = [];
let ID, ID2;
let PRE_COL;

let POSX = [];
let POSY = [];
let C1 = 7;
let C4 = 100;
let C02;
let C2 = 4;

let FRAME1 = 360; //360;
let FRAME2 = 180;
let FRAME3 = 180;
let FRAME4 = 90;

let BACK_COL;

let T_TYPE = [];
let T_SPEED = [];
let T_RAD = [];
let T_MCOH = [];
let T_ICOH = [];

let T_RED = [];
let T_GREEN = [];
let T_BLUE = [];

let OS_X = [];
let OS_Y = [];

function setup() {
  W = windowWidth;
  H = windowHeight;
  createCanvas(W, H);
  G = createGraphics(W, H);

  //SEED = fxrand()*998244353;
  SEED = random() * 998244353;

  randomSeed(SEED);
  noiseSeed(SEED);

  G.rectMode(CENTER);
  G.noStroke();
  let bsel = int(random(2));
  let bcol = [60, 200];
  BACK_COL = bcol[bsel];


  ID = [0, 1, 2];
  ID2 = [0, 1, 2];
  shuffle(ID, true);
  shuffle(ID2, true);

  PRE_COL = [random(75) + 70, random(75) + 35, random(35)];

  for (let i = 0; i < C1; i++) {
    POSX[i] = random(-W / 3, W / 3) + W / 2;
    POSY[i] = random(-H / 4, H / 2) + H / 2;
  }

  let cycle1 = [120, 180, 360];
  let csel = int(random(3));
  let mx_coh = 1.0; //random(1.3, 1.5);
  flocks.push(new Flock());
  for (let i = 0; i < 500; ++i) {
    flocks[0].add(
      new Particle(
        0,
        0,
        7.5,
        150,
        FRAME1,
        cycle1[csel],
        0.0,
        mx_coh,
        10,
        int(random(C1)),
      ),
    );
  }

  T_RED = [];
  T_GREEN = [];
  T_BLUE = [];
  for (let i = 0; i < C4; ++i) {
    POSX[i] = random(-W / 2, W / 2) + W / 2;
    POSY[i] = random(-H / 2, H / 2) + H / 2;
    T_RED.push(random(30, 220));
    T_GREEN.push(random(30, 220));
    T_BLUE.push(random(30, 220));
  }

  let cycle4 = [45, 90];
  mx_coh = 0.6;
  flocks.push(new Flock());
  for (let i = 0; i < 500; ++i) {
    let csel4 = int(random(2));
    flocks[1].add(
      new Particle(
        2,
        1,
        1.5,
        20,
        FRAME4,
        cycle4[csel4],
        0.0,
        mx_coh,
        10,
        int(random(C4)),
      ),
    );
  }

  let mode = 1;
  if (mode == 1) {
    for (let ii = 0; ii < 2; ++ii) {
      let X2 = [];
      let n_tree = int(random(6, 10));
      let fdiv = 2;
      if (ii == 1) {
        n_tree = int((n_tree / 3) * 2);
        fdiv = 2.5;
      }
      for (let i = 0; i < n_tree; ++i) {
        X2.push(random(-W / fdiv, W / fdiv));
      }
      C02 = n_tree;
      for (let i = 0; i < C02; ++i) {
        POSX[i] = X2[i] + W / 2 + random(-15, 15);
        POSY[i] = random(-H / 2, -H / 3) + H / 3 + (H / 6) * ii; //top
      }

      T_TYPE = [];
      T_SPEED = [];
      T_RAD = [];
      T_MCOH = [];
      T_ICOH = [];

      T_RED = [];
      T_GREEN = [];
      T_BLUE = [];

      OS_X = [];
      OS_Y = [];

      for (let i = 0; i < C02; ++i) {
        T_TYPE.push(0); //int(random(2)));
        T_RED.push(random(50, 200));
        T_GREEN.push(random(50, 200));
        T_BLUE.push(random(50, 200));

        OS_X.push(random(-100, 100));
        OS_Y.push(random(H / 3));
      }

      FRAME2 = 180;
      let cycle1 = [180];
      let csel1 = int(random(1));
      mx_coh = 3;
      flocks.push(new Flock());
      for (let i = 0; i < 300; ++i) {
        let speed = random(6, 12);
        flocks[ii * 2 + 2].add(
          new Particle(
            1,
            1,
            speed,
            20,
            FRAME2,
            cycle1[csel1],
            0.5,
            mx_coh,
            ii * 10 + 15,
            int(random(C02)),
          ),
        );
      }

      T_SPEED = [2.5, 2];
      T_MCOH = [0.3, 1.0];
      T_ICOH = [0.0, 0.0];
      T_RAD = [20, 20];

      FRAME3 = 180;
      let cycle2 = [90, 180];

      flocks.push(new Flock());
      for (let i = 0; i < 500; ++i) {
        let csel2 = int(random(2));
        let tp_sel = int(random(C02));
        let tp = T_TYPE[tp_sel];
        flocks[ii * 2 + 3].add(
          new Particle(
            1,
            2,
            T_SPEED[tp],
            T_RAD[tp],
            FRAME3,
            cycle2[csel2],
            T_ICOH[tp],
            T_MCOH[tp],
            ii * 10 + 5,
            tp_sel,
          ),
        );
      }
    }
  }

  //let X2 = random(-W/2, W/2) + W/2;
  let Y2 = random(-H / 4, H / 2) + H / 2;
  for (let i = 0; i < C2; ++i) {
    POSX[i] = random(-W / 2, W / 2) + W / 2;
    POSY[i] = random(-H / 4, H / 4) + Y2;
  }
}

function draw() {
  blendMode(BLEND);
  background(BACK_COL);

  if (frameCount < FRAME1) {
    flocks[0].run();
  } else if (frameCount < FRAME1 + FRAME4) {
    flocks[1].run();
  } else if (frameCount < FRAME1 + FRAME4 + FRAME2) {
    flocks[2].run();
  } else if (frameCount < FRAME1 + FRAME4 + FRAME2 + FRAME3) {
    flocks[3].run();
  } else if (frameCount < FRAME1 + FRAME4 + FRAME2 * 2 + FRAME3) {
    flocks[4].run();
  } else if (frameCount < FRAME1 + FRAME4 + FRAME2 * 2 + FRAME3 * 2) {
    flocks[5].run();
  }

  image(G, 0, 0, width, height, 0, 0, W, H);
}

class Flock {
  particles;

  constructor() {
    this.particles = [];
  }

  add(particle) {
    this.particles.push(particle);
  }

  run() {
    this.update();
    this.display();
  }

  update() {
    for (let i = 0; i < this.particles.length; ++i) {
      this.particles[i].update(this.particles);
    }
  }

  display() {
    for (let i = 0; i < this.particles.length; ++i) {
      this.particles[i].display();
    }
  }
}

class Particle {
  acceleration;
  velocity;
  position;
  radius;
  max_r;
  color;
  col_sep;
  maxforce = 1.0;
  maxspeed = 2.5;
  minspeed = 0.001;
  mode;
  base;
  ali_f = 0.0;
  coh_f = 0.0;
  max_coh;
  alpha;
  fcnt;
  cycle;
  lcnt;
  fin;

  constructor(mode, base, speed, max_r, lcnt, cycle, coh, mxcoh, alpha, sel) {
    this.acceleration = createVector(0, 0);

    this.mode = mode;
    this.base = base;
    let offset_x;
    let offset_y;

    this.radius = 1;
    this.alpha = alpha;

    //let sel;
    if (this.base == 0) {
      this.maxspeed = speed;
      this.col_sep = 0.5;
      this.position = createVector(POSX[sel], POSY[sel]);
      this.max_r = max_r;
      this.velocity = createVector(random(-1, 1), random(-1, 1));
    }
    if (this.mode == 1) {
      if (this.base == 1) {
        offset_y = H / 2;
        this.maxspeed = speed;
        this.col_sep = 0.5;
        this.position = createVector(
          POSX[sel] + random(-5, 5),
          POSY[sel] + offset_y,
        );
        this.max_r = max_r;
        this.velocity = createVector(random(-1, 1), random(0.5, 1)); //;createVector(random(-1, 1), random(-1, 1));
      } else if (this.base == 2) {
        if (T_TYPE[sel] == 0) {
          offset_y = random(H / 2);
          this.maxspeed = speed * (offset_y / H) * (offset_y / H) * 5;
          this.col_sep = 0.5;
          this.position = createVector(POSX[sel], max(5, POSY[sel] + offset_y));
          this.max_r = max_r + (offset_y / H) * 50;
          this.velocity = createVector(random(-1, 1), random(0.1, 1));
        } else {
          offset_x = OS_X[sel];
          offset_y = OS_Y[sel];
          this.maxspeed = speed;
          this.col_sep = 0.5;
          this.position = createVector(
            POSX[sel] + offset_x,
            POSY[sel] + offset_y,
          );
          this.max_r = max_r;
          this.velocity = createVector(random(-1, 1), random(-1, 1));
        }
      }
    } else if (this.mode == 2) {
      let ratio = POSY[sel] / H + 0.2;
      this.maxspeed = speed * 1.2 * ratio;
      this.radius = this.radius * 1.2 * ratio;
      this.alpha = this.alpha * 1.2 * ratio;
      if (this.base == 1) {
        this.col_sep = 0.5;
        this.position = createVector(POSX[sel], POSY[sel]);
        this.max_r = max_r;
        this.velocity = createVector(random(-1, 1), random(-1, 1));
      }
      // else if(this.base == 2){
      //     this.col_sep = 0.5;
      //     this.position = createVector(POSX[sel], POSY[sel]);
      //     this.max_r = max_r;
      //     this.velocity = createVector(random(-1, 1), random(-1, 1));
      // }
    }

    //color
    if (this.base == 0) {
      this.color = createVector(
        max(0, PRE_COL[ID[0]] + random(-50, 30)),
        max(0, PRE_COL[ID[1]] + random(-50, 30)),
        max(0, PRE_COL[ID[2]] + random(-50, 30)),
      );
    }
    if (this.mode == 1) {
      if (this.base == 1) {
        this.color = createVector(
          min(
            255,
            max(0, 200 - PRE_COL[ID[0]] + T_RED[sel] / 6 + random(-100, 0)),
          ),
          min(
            255,
            max(0, 200 - PRE_COL[ID[1]] + T_GREEN[sel] / 6 + random(-100, 0)),
          ),
          min(
            255,
            max(0, 200 - PRE_COL[ID[2]] + T_BLUE[sel] / 6 + random(-100, 0)),
          ),
        );
      } else if (this.base == 2) {
        this.color = createVector(
          min(255, max(0, T_RED[sel] + random(-50, 50))),
          min(255, max(0, T_GREEN[sel] + random(-50, 50))),
          min(255, max(0, T_BLUE[sel] + random(-50, 50))),
        );
      }
    } else if (this.mode == 2) {
      if (this.base == 1) {
        this.color = createVector(
          min(255, max(0, T_RED[sel] + random(-30, 30))),
          min(255, max(0, T_GREEN[sel] + random(-30, 30))),
          min(255, max(0, T_BLUE[sel] + random(-30, 30))),
        );
      } else if (this.base == 2) {
        this.color = createVector(
          max(100, min(255, PRE_COL[ID[0]] + random(0, 100))),
          max(100, min(255, PRE_COL[ID[1]] + random(0, 100))),
          max(100, min(255, PRE_COL[ID[2]] + random(0, 100))),
        );
      }
    }

    this.ali_f = coh;
    this.coh_f = coh;
    this.max_coh = mxcoh;

    this.fcnt = 0;
    this.cycle = cycle;
    this.fin = false;
    this.lcnt = lcnt;
  }

  applyForce(force) {
    this.acceleration.add(force);
  }

  inner_update(particles) {
    let sep = this.separate(particles);
    let ali = this.align(particles);
    let coh = this.cohesion(particles);

    sep.mult(1.5);
    ali.mult(this.ali_f);
    coh.mult(this.coh_f);

    this.applyForce(sep);
    this.applyForce(ali);
    this.applyForce(coh);
  }

  update(particles) {
    if (this.fcnt >= this.lcnt) {
      this.fin = true;
    }
    if (this.fin) {
      return;
    }
    this.inner_update(particles);

    this.velocity.add(this.acceleration);
    if (this.base == 0) {
      if (this.fcnt < this.lcnt / 2) {
        this.velocity.add(createVector(0, -0.1));
      }
    } else if (this.mode == 1 && this.base == 1) {
      this.velocity.add(createVector(0.0, 0.2));
    } else {
      if (this.fcnt < this.lcnt / 2) {
        this.velocity.add(createVector(-0.0, 0));
      }
    }

    this.velocity.limit(this.maxspeed);
    this.position.add(this.velocity);
    this.border();
    this.acceleration.mult(0);

    this.radius = abs(sin((this.fcnt / this.cycle) * PI)) * this.max_r + 1;

    if (this.base == 0) {
      this.ali_f = min(1.0, this.ali_f + 0.05);
      this.coh_f = min(this.max_coh, this.coh_f + 0.05);
    } else if (this.mode == 1 && this.base == 1) {
      this.ali_f = min(1.5, this.ali_f + 0.03);
      this.coh_f = min(this.max_coh, this.coh_f + 0.05);
    } else {
      this.ali_f = min(0.7, this.ali_f + 0.1);
      this.coh_f = min(this.max_coh, this.coh_f + 0.05);
    }

    if (this.fcnt < this.lcnt) {
      this.fcnt += 1;
    }
  }

  border() {
    //if(this.base != 1 || this.mode != 1){
    if (this.position.x > W) {
      this.position.x -= W;
    } else if (this.position.x < 0) {
      this.position.x += W;
    }
    if ((this.base != 1 || this.mode != 1) && this.mode != 2) {
      if (this.position.y > H) {
        this.position.y -= H;
      } else if (this.position.y < 0) {
        this.position.y += H;
      }
    }
  }

  seek(target) {
    let v = p5.Vector.sub(target, this.position);
    v.normalize();
    v.mult(this.maxspeed);
    v.sub(this.velocity);
    v.limit(this.maxforce);
    return v;
  }

  separate(particles) {
    let inter_range = 128.0;
    let v = createVector(0, 0, 0);
    for (let i = 0; i < particles.length; i++) {
      let d = p5.Vector.dist(this.position, particles[i].position);
      if (d <= 0) continue;

      let col_diff = p5.Vector.sub(this.color, particles[i].color).mag() - 110;
      if (d < inter_range + col_diff * this.col_sep) {
        let diff = p5.Vector.sub(this.position, particles[i].position);
        diff.normalize();
        diff.div(d);
        v.add(diff);
      }
    }

    if (v.mag() > 0) {
      v.normalize();
      v.mult(this.maxspeed);
      v.sub(this.velocity);
      v.limit(this.maxforce);
    }

    return v;
  }

  align(particles) {
    let inter_range = 128.0;
    let v = createVector(0, 0, 0);
    let count = 0;
    for (let i = 0; i < particles.length; i++) {
      let d = p5.Vector.dist(this.position, particles[i].position);
      //let col_diff = abs(this.color - particles[i].color) - 128;
      let col_diff = p5.Vector.sub(this.color, particles[i].color).mag() - 110;
      if (d > 0 && d < inter_range - col_diff * this.col_sep) {
        v.add(particles[i].velocity);
        count++;
      }
    }
    if (count > 0) {
      v.div(count);
      v.normalize();
      v.mult(this.maxspeed);
      v.sub(this.velocity);
      v.limit(this.maxforce);
      return v;
    } else {
      return createVector(0, 0, 0);
    }
  }

  cohesion(particles) {
    let neighbor_range = 128.0;
    let v = createVector(0, 0, 0);
    let count = 0;
    for (let i = 0; i < particles.length; i++) {
      let d = p5.Vector.dist(this.position, particles[i].position);

      let col_diff = p5.Vector.sub(this.color, particles[i].color).mag() - 110;
      if (d > 0 && d < neighbor_range - col_diff * this.col_sep) {
        v.add(particles[i].position);

        count++;
      }
    }
    if (count > 0) {
      v.div(count);
      return this.seek(v);
    } else {
      return createVector(0, 0, 0);
    }
  }

  display() {
    if (this.fin) {
      return;
    }
    let ratio = 1.0;
    //if (this.base == 0){
    //ratio = (H - this.position.y) / H * 1. + random(1.0);
    ratio = 0.8 + random(0.5);
    //}
    if (this.base == 0) {
      ratio = ratio * (3.0 - 3 * sqrt(this.position.y / H) * random(0.8, 1.2));
    }
    if (this.mode == 1 && this.base == 2) {
      ratio = ratio + random(-0.2, 1);
    }
    G.strokeWeight(this.radius);
    G.stroke(
      this.color.x * ratio,
      this.color.y * ratio,
      this.color.z * ratio,
      this.alpha,
    );
    G.point(this.position.x, this.position.y);
  }
}
