let colors = ["#c78b25", "#4e5b6d", "#636b4b", "#2b5c72", "#973535", "#ad9690", "#15171f"]

let drops = []
let splatters = []
let straps = []
let simplex

function setup() {
	createCanvas(windowWidth, windowHeight);
	simplex = new openSimplexNoise(Date.now())
	background(210);
	let x = random(width)
	let s = noise(x / 100) * width / 50
	drops.push(new drop(x, s))
}

function draw() {
	if (frameCount % 50 == 0 && frameCount < 2500) {

		let x = map(simplex.noise2D(frameCount / 300, random()), -1, 1, -width / 4, width + width / 4)
		let s = noise(x / 100) * width / 50
		drops.push(new drop(x, s))
    straps.push(new strap(random(width), random(height / 5, height / 2.5)))
		straps.push(new strap(random(width), random(height / 5, height / 2.5)))

	}


	if (frameCount % 100 == 0 && frameCount > 500 && frameCount < 4000) {
		splatters.push(new splatter(random(width), random(0, height * 0.66), random(height * 0.02, height * 0.1)))
	}
	for (let d of drops) {
		d.move();
		d.show();
		d.done();
	}
	for (let s of splatters) {
		s.move();
		s.show();
		s.done();
	}
	for (let st of straps) {
		st.move();
		st.show();
		st.done();
	}

}

class splatter {
	constructor(x, y, r) {
		this.pos = createVector(x, y);
		this.color1 = random(colors)
		this.color2 = random(colors)
		this.r = r
		this.currentR = 0
		this.runs = []
		let amount = floor(random(15, 30))
		let startAngle = random(TAU)
		for (let a = 0; a < TAU; a += TAU / amount) {
			let aOff = random(-TAU / 12, TAU / 12)
			let sizeRange = this.r / (amount / 4)
			var sp = {
				angle: a + aOff,
				size: random(sizeRange * 0.75, sizeRange * 1.5)
			}
			this.runs.push(sp)
		}
		this.speed = this.r / 40
	}

	move() {
		this.currentR += this.speed
	}

	show() {
		let mixer = map(this.currentR, 0, this.r, 0, 1)
		let filler = color(spectral.mix(this.color1, this.color2, mixer))
		// let alpha= map(this.currentR, 0, this.r, 2, 100)
		// filler.setAlpha(alpha)
		fill(filler)

		//probably need to add some jiggle
		for (let r of this.runs) {
			let quaver = simplex.noise2D(r.angle, this.currentR / (this.r / 3)) * r.size / 4
			let xoff = simplex.noise2D(r.angle, this.currentR / 100, this.pos.x) * this.currentR / 5
			let yoff = noise(r.angle, this.currentR / 100, this.pos.y) * this.currentR
			ellipse(this.pos.x + cos(r.angle) * this.currentR + xoff, this.pos.y + sin(r.angle) * this.currentR + yoff, r.size + quaver)
		}
	}

	done() {
		if (this.currentR >= this.r) {
			let index = splatters.indexOf(this);
			splatters.splice(index, 1);
		}

	}
}

class drop {
	constructor(x, s) {
		this.pos = createVector(x, -s)
		this.color1 = random(colors)
		this.color2 = random(colors)
		this.size = s
		this.speed = random(1, 5)
	}

	move() {
		let xdrift = simplex.noise2D(this.pos.x / 100, frameCount / 50) * this.size / 10
		let mover = createVector(xdrift, this.speed)
		this.pos.add(mover)

	}

	show() {
		noStroke()
		let quaver = simplex.noise3D(this.pos.y / 200, this.pos.x / 200, frameCount / 50) * this.size / 2
		let m = map(this.pos.y, 0, height, 0, 1)
		let mixer = color(spectral.mix(this.color1, this.color2, m))
		let a = map(this.size, width / 100, width / 10, 75, 1)
		mixer.setAlpha(a)
		fill(mixer)
		ellipse(this.pos.x, this.pos.y, this.size + quaver)
	}

	done() {
		if (this.pos.y > this.size + height) {
			let index = drops.indexOf(this);
			drops.splice(index, 1);
		}

	}
}

class strap {
	constructor(x, h) {
		this.h = h
		this.w = random(width / 100, width / 40)
		this.pos = createVector(x, height + this.w)
		this.color1 = random(colors)
		this.color2 = random(colors)
	}

	move() {
		this.pos.y -= 1
	}

	show() {
		push()
		let mix = map(this.pos.y, height, height - this.h, 0, 1)
		let filler = color(spectral.mix(this.color1, this.color2, mix))
		fill(filler)
		let curve = map(this.pos.y, height, height - this.h, 0, this.h)
		let xoff = simplex.noise3D(this.pos.x, this.pos.y / (this.h * 2), frameCount / 200) * curve
		let sizing = map(this.pos.y, height, height - this.h, this.w, 1)
		ellipse(this.pos.x + xoff, this.pos.y, sizing)
pop()
	}

	done() {
		if (this.pos.y < height - this.h) {
			let index = straps.indexOf(this);
			straps.splice(index, 1);
		}
	}
}

