let particles=[]



function setup() {
	let w=min(windowWidth, windowHeight)
	createCanvas(windowWidth, windowHeight, WEBGL);
	colorMode(HSB, 100)
	particles.push(new particle())
}

function draw() {
	background(15, 10, 98);
	particles.push(new particle())
	for(let particle of particles){
		particle.update()
		particle.display()
	}
}

class particle{
constructor(){
	this.a=random(TAU)
	this.size=1
	this.hue=70
	this.brightness=100
	this.alpha=100
	this.y=0
}

update(){
	this.size+=0.15
	this.hue-=0.05
	this.brightness-=0.02
	this.y+=0.2
	this.alpha-=0.07

	if(this.alpha<0.1){
		let index = particles.indexOf(this);
	particles.splice(index, 1);
	}
}

display() {
    push();
    noStroke();
    // Removed unnecessary translate to the center
    rotate(this.a); // Apply rotation
    fill(this.hue % 100, 80, this.brightness, this.alpha); // Apply color with fading
    ellipse(0, this.y, this.size); // Draw particle
    pop();
  }


}

