let colors111 = "f2f2f2-d9d9d9-595959-262626-0d0d0d".split("-").map((a) => "#" + a);
let colors1111 = "2450A6-639CBF-F2EFBD-F28705-F2380F".split("-").map((a) => "#" + a);
let color_shapes;
