function setup() {
	// frameRate(25);
	mySize = min(windowWidth, windowHeight);
	// mySize = 1000;
	margin = mySize / 100;
	// createCanvas(windowWidth, windowHeight);
	 createCanvas(windowWidth, windowHeight);
	colorMode(HSB, 360, 100, 100, 100);
	// pixelDensity(5);
	r = mySize / random(4, 7);
	color_shapes = colors111;
	background(colorbg);
	let filter1 = new makeFilter();
}

function draw() {
	randomSeed(seed);
	bg_lines();
