			for (let i = 0; i < i_num; i++) {
				push();
				rotate(TAU / i_num * i);
				noFill();
				stroke("#f2f2f2");
				strokeWeight(r / 50);
				line(0, 0, r / 1.95 * cos(b * (th + seg)), r / 1.95 * cos(b * (th + seg)));
				pop();
			}

			//body
			fill("#b80000");
			ellipse(0, 0, r / 1 * cos(b * (th + seg)), r / 2 * cos(b * (th + seg)));

			// arms & hands
			fill("#f2f2f2");
			rectMode(CENTER);
			rect(0, 0, r / 0.45 * cos(b * (th + seg)), r / 8 * cos(b * (th + seg)));
