			drawingContext.shadowColor = "#20202040";
			drawingContext.shadowOffsetX = 1;
			drawingContext.shadowOffsetY = 1;
			drawingContext.shadowBlur = 5;
			//head
			fill("#f2f2f2");
			circle(0, 0, r / 4 * (0.75 + abs(cos(a * (th + seg))) / 2));
			let i_num2 = 6;
			for (let i = 0; i < i_num2; i++) {
