function bg_lines() {
	noFill();
	stroke("#f2f2f2");
	strokeWeight(random(0.1, 0.1) * 10);

	//middle
	for (let i = 0; i < 10; i++) {
		randomSeed(seed * i);
		let line_x = random(width * 0.4, width * 0.6);

		line(line_x, 0, line_x, height * 0.75);
	}
	strokeCap(PROJECT);
	line(width * 0.4, 0, width * 0.4, height * 0.75);
	line(width * 0.6, 0, width * 0.6, height * 0.75);
	line(width * 0.4, height * 0.75, width * 0.6, height * 0.75);

	//left
	line(0, height * 0.75, width * 0.2, height * 0.75);
	line(width * 0.2, height * 0.75, width * 0.2, height * 0.8);
	line(width * 0.2, height * 0.8, width * 0.3, height * 0.8);
	line(width * 0.3, height * 0.8, width * 0.3, height * 0.85);
	line(width * 0.3, height * 0.85, width * 0.4, height * 0.85);
	line(width * 0.4, height * 0.85, width * 0.4, height * 0.9);
	line(width * 0, height * 0.9, width * 0.4, height * 0.9);

	//right
	triangle(width*1.1, height * 0.85, width * 0.7, height * 0.85, width*1.1, height * 0.8);
}




function makeFilter() {
	let filterNum = int(Math.random() * 982);
