	line(width * 0.3, height * 0.85, width * 0.4, height * 0.85);
	line(width * 0.4, height * 0.85, width * 0.4, height * 0.9);
	line(width * 0, height * 0.9, width * 0.4, height * 0.9);

	//right
	triangle(width*1.1, height * 0.85, width * 0.7, height * 0.85, width*1.1, height * 0.8);
}




